function OpenMenu(MenuID, Box) {
    try {
        if (Box.classList.contains("menu-hover")) {
            var MenuElement = document.querySelectorAll(`div[menu-id="${MenuID}"]`);
            for (let i = 0; i < MenuElement.length; i++) {
                let CurrentElement = MenuElement[i];
                if (CurrentElement.classList.contains("menu-hover")) {
                    CurrentElement.click()
                }
                setTimeout(function() {
                    CurrentElement.style.translate = "-100%";
                    setTimeout(function() { CurrentElement.style.display = "none" }, 50)
                }, i * 100); // i * 200 will create a delay that increases with each iteration
            }
        }
        else {
            var MenuElement = document.querySelectorAll(`div[menu-id="${MenuID}"]`);
            for (let i = 0; i < MenuElement.length; i++) {
                let CurrentElement = MenuElement[i];
                setTimeout(function() {
                    CurrentElement.style.display = "flex";
                    setTimeout(function() { CurrentElement.style.translate = "0" }, 50);
                }, i * 100); // i * 200 will create a delay that increases with each iteration
            }
        }
        Box.classList.toggle("menu-hover")
    }
    catch{
        var MenuElement = document.querySelectorAll(`div[menu-id="${MenuID}"]`);
        for (let i = 0; i < MenuElement.length; i++) {
            let CurrentElement = MenuElement[i];
            setTimeout(function() {
                CurrentElement.style.display = "flex";
                setTimeout(function() { CurrentElement.style.translate = "0" }, 50);
            }, i * 100); // i * 200 will create a delay that increases with each iteration
        }
    }
}

// Flashcard Stuff:

var TopicsInUse = []
var TopicTitles = []

function ToggleTopic(TopicID, TopicTitle) {
    if (TopicsInUse.includes(TopicID)) { // remove the topic
        TopicsInUse.splice(TopicsInUse.indexOf(TopicID), 1)
        TopicTitles.splice(TopicsInUse.indexOf(TopicTitle), 1)
    }
    else {
        TopicsInUse.push(TopicID)
        TopicTitles.push(TopicTitle)

        NewQuestion()
    }

    document.querySelector(".popup-text").innerHTML = TopicTitles.join("<br>")
}

function NewQuestion() {
    let RandomTopic = TopicsInUse[Math.floor(Math.random()*TopicsInUse.length)]
    let RandomArray = Flashcards[RandomTopic];
    let RandomCard = RandomArray[Math.floor(Math.random()*RandomArray.length)]
    console.log(RandomCard)

    let Card = document.querySelector(".front-card")
    let CardText = Card.querySelector(".text")
    let CardLink = Card.querySelector(".link")
    let CardHeader = Card.querySelector(".header")

    Card.setAttribute("Question", RandomCard.question)
    Card.setAttribute("Answer", RandomCard.answer)
    Card.setAttribute("Step", "1")

    Card.onclick = FlipCard

    for (let i = 0; i < Menus.length; i++) {
        let Menu = Menus[i]
        if (Menu.OnclickID == RandomTopic) {
            CardLink.innerHTML = `<a href="../../../${Menu.Link}"><ul>Learn More</ul></a>`
            CardHeader.textContent = Menu.Title
        }
    }

    CardText.innerHTML = `Question:<br><br>${RandomCard.question}`
}
function NewCard() {
    let Card = document.querySelector(".decoy")
    //Card.style.translate = "0 -35px"

    Card.innerHTML = document.querySelector(".front-card").innerHTML

    Card.style.transition = "0s"
    Card.style.translate = "0 -35px"
    Card.style.zIndex = "999"
    setTimeout(function() {
        Card.style.transition = "0.8s"
        Card.style.translate = "0 -150vh"
        setTimeout(function() {
            Card.style.transition = "0s"
            Card.style.translate = "0 -5px"
            Card.style.zIndex = "auto"
            Card.innerHTML = ""
        }, 400)
    }, 100)

    NewQuestion()
}
function FlipCard() {

    this.style.transition = "1s"
    this.style.transform = "rotateY(180deg)"
    setTimeout(function() {
        if (document.querySelector(".front-card").getAttribute("Step") == "1") {
            document.querySelector(".front-card").querySelector(".text").innerHTML = `Answer:<br><br>${document.querySelector(".front-card").getAttribute("Answer")}`
            document.querySelector(".front-card").setAttribute("Step", "2")
        }
        else {
            document.querySelector(".front-card").querySelector(".text").innerHTML = `Question:<br><br>${document.querySelector(".front-card").getAttribute("Question")}`
            document.querySelector(".front-card").setAttribute("Step", "1")
        }
        setTimeout(function() {
            document.querySelector(".front-card").style.transition = "0s"
            document.querySelector(".front-card").style.transform = "rotateY(0deg)"
        }, 300)
        
        
    }, 200)
}

